SELECT OBJECT_NAME(i.object_id) AS [Table Name], i.name AS [Index Name], str(p.used_page_count * 8.0 / 1024 / 1024, 10, 2) as UsedGB, str(p.reserved_page_count * 8.0 / 1024 / 1024, 10, 2) as ReservedGB, s.user_seeks, s.user_scans, s.user_lookups, s.user_updates,  s.last_user_seek, s.last_user_scan, s.last_user_lookup, s.last_user_update,  s.system_seeks, s.system_scans, s.system_lookups, s.system_updates,  s.last_system_seek, s.last_system_scan, s.last_system_lookup, s.last_system_update, i.is_disabled FROM sys.dm_db_index_usage_stats AS s JOIN sys.indexes AS i ON i.object_id = s.object_id AND i.index_id = s.index_id JOIN sys.objects AS o ON o.object_id = s.object_id join sys.dm_db_partition_stats p on s.object_id = p.object_id and s.index_id = p.index_id WHERE s.database_id = DB_ID() AND OBJECT_NAME(i.object_id) = 'Documents'--'ArtSent' order by i.is_disabled